CREATE TABLE CART (CART_ID NUMERIC(10,0) NOT NULL, COUNTRY_CODE CHARACTER VARYING(10) NOT NULL, CURRENCY CHARACTER VARYING(5) NOT NULL, CREATE_TS TIMESTAMP(6) WITHOUT TIME ZONE NOT NULL, UPDATE_TS TIMESTAMP(6) WITHOUT TIME ZONE, PRIMARY KEY (CART_ID));
CREATE TABLE PRODUCT ( id UUID NOT NULL, CART_ID NUMERIC(10,0) NOT NULL, DESCRIPTION CHARACTER VARYING(100) NOT NULL, CATEGORY CHARACTER VARYING(25) NOT NULL, PRICE NUMERIC(9,2)  NOT NULL, CREATE_TS TIMESTAMP(6) WITHOUT TIME ZONE NOT NULL, UPDATE_TS TIMESTAMP(6) WITHOUT TIME ZONE,PRIMARY KEY (id), CONSTRAINT fk_Product_cart FOREIGN KEY (CART_ID) REFERENCES CART (CART_ID) ON DELETE CASCADE);